import { wordList } from "../Data/wordList";

const ResultsProvider = ( rows ) => {
    let filteredList = [...wordList[0]];

    for( let row of rows ) {
        console.log(row);
        filteredList = filteredList.filter((word) => {
            // check if word matches row
            console.log( word);
            for (let {i, char} of row['array'].entries()) {

                if (char === null || char === '' || char === ' ') continue;

                if (row.color === "green" && char !== word[i]) {
                    return false;
                }
                if (row.color === "yellow" && (!(char in word) || char === word[i])) {
                    return false;
                }
                if (row.color === "grey" && char in word) {
                    return false;
                }
            }
            return true;
        });
        console.log(filteredList);

    }   
    return filteredList
};

