import React, { useState } from "react";
import GreenRow from '../GreenRow/GreenRow';
import YellowRow from '../YellowRow/YellowRow';
import "./RowList.css";
import ResultsProvider from "../ResultsProvider";

const RowList = () => {
    const [rowStates, setRowStates] = useState([{'array': [], 'color': "green"}, {'array': [], 'color': "yellow"}]);
    const [results, setResults] = useState('');

    const createYellow = () => {
        if (rowStates.length >= 5) return;
        setRowStates([...rowStates, {array: null, color: "green"}]);
    };

    const deleteYellow = () => {
        if (rowStates.length <= 2) return;
        setRowStates(rowStates.slice(0, -1));
    };

    const handleInputChange = (index, letterList) => {
      const newRowStates = [...rowStates];
      newRowStates[index] = letterList;
      setRowStates(newRowStates);
    };

    const getRowComponent = (index) => {
        if (index === 0) {
            return <GreenRow key={index} value={rowStates[index]['array']} onChange={(e) => handleInputChange(index, e)} />;
        } else {
            return <YellowRow key={index} value={rowStates[index].array} onChange={(e) => handleInputChange(index, e)} />;
        }
    };

    const getResults = () => {
        console.log(rowStates);
        const results = ResultsProvider(rowStates);
        console.log(results);
        setResults(results);
    }

    return(
        <div>
            <button onClick={createYellow}>Add Row</button>
            <button onClick={deleteYellow}>Delete Row</button>
            <button onClick={getResults}>Submit</button>

            {/* create elements in rows state */}
            {rowStates.map((rowState, index) => (
                <div key={index}>
                    {getRowComponent(index)}
                </div>
            ))}
            <div className="results">
                {results ? results.map((result) => (
                    <text style={{color: "black"}}>{result}, </text>
                ))
                :''}
            </div>
        </div>

    );
}

export default RowList;
