import React, { useEffect, useState, useRef } from "react";
import "./Row.css";

const Row = () => {
    const [wordList, setWordList] = useState(Array(5).fill(''));
    const inputRefs = useRef([]);

    const handleInputChange = (index, event) => {
        const newWordList = [...wordList];
        newWordList[index] = event.target.value.toUpperCase();
        setWordList(newWordList);

        if ((event.target.value === "" || event.keyCode === 8) && index > 0) {
            inputRefs.current[index - 1].focus();
        }

        if (event.target.value !== "" && index < 4) {
            inputRefs.current[index + 1].focus();
        }
    };

    useEffect(() => {
        inputRefs.current[0].focus();
    }, []);


    return(
        <div className="Row">
            {/* create 5 text input fields using a loop*/}
            {[...Array(5).keys()].map((index) => (
                <input 
                    key={index} 
                    ref={(ref) => (inputRefs.current[index] = ref)}
                    index={index}
                    value={wordList[index]} 
                    maxLength={1} 
                    onChange={(e) => handleInputChange(index, e)}
                />
            ))}
        </div>
    );
};

export default Row;
