import word_list from "../Data/word_list.txt";

const ResultsProvider = ( rows ) => {
    console.log(rows);
    for( let row of rows ) {
        word_list = word_list.filter((word) => {

            // check if word matches row
            for (let [i, char] of row.entries()) {
                if (row === "green" && char !== word[i]) {
                    return false;
                }
                if (row === "yellow" && (!(char in word) || char === word[i])) {
                    return false;
                }
                if (row === "grey" && char in word) {
                    return false;
                }
            }
            return true;

        });
    }   
    return word_list
};

export default ResultsProvider;

