import React, { useEffect, useState } from "react";
import Row from '../Row/Row';
import "./RowList.css";
import ResultsProvider from "../ResultsProvider";

const RowList = () => {
    const [rowStates, setRowStates] = useState([{'array': [], 'color': "green"}, {'array': [], 'color': "yellow"}]);
    const [results, setResults] = useState('');
    const [indexRow, setIndex] = useState(0);
    const [indexCol, setCol] = useState(0);

    const isAlpha = function(ch){
        return /^[A-Z]$/i.test(ch);
    }

    useEffect(() => {
        const handleKeyDown = (event) => {
            const { key } = event;
            if (key === "ArrowUp"  && indexRow > 0){
                setIndex(indexRow - 1);
            }
            else if (key === "ArrowDown" && indexRow < rowStates.length - 1){
                setIndex(indexRow + 1);
            }
            else if (key === "ArrowLeft" && indexCol > 0){
                setCol(indexCol - 1);
            }
            else if (key === "ArrowRight" && indexCol < 4){
                setCol(indexCol + 1);
            }
            else if (key === "Backspace"){
                // delete letter
                const newRowStates = [...rowStates];
                newRowStates[indexRow]['array'][indexCol] = '';
                setRowStates(newRowStates);
                if (indexCol > 0) setCol(indexCol - 1);
            }
            else if (isAlpha(key)) {
                const newRowStates = [...rowStates];
                newRowStates[indexRow]['array'][indexCol] = key;
                setRowStates(newRowStates);
                if (indexCol < 4) setCol(indexCol + 1);
            }
        };
        document.addEventListener("keydown", handleKeyDown);
        return () => {
            document.removeEventListener("keydown", handleKeyDown);
        };
    }, [indexRow, indexCol, rowStates.length]);


    const createYellow = () => {
        if (rowStates.length >= 5) return;
        setRowStates([...rowStates, {'array': [], 'color': "yellow"}]);
    };

    const deleteYellow = () => {
        if (rowStates.length <= 2) return;
        setRowStates(rowStates.slice(0, -1));
    };

    const handleInputChange = (index, letterList) => {
      const newRowStates = [...rowStates];
      newRowStates[index]['array'] = letterList;
      setRowStates(newRowStates);
    };

    const clearResults = () => {
        setResults('');
    }

    const getRowComponent = (index) => {
        const color = index === 0 ? "green" : "yellow";
        return (
                <Row 
                    key={index} 
                    value={rowStates[index]['array']} 
                    color={color} 
                    isFocused={index === indexRow}
                    column={indexCol}
                />
            );
    };

    const getResults = () => {
        const results = ResultsProvider(rowStates);
        setResults(results);
    }

    return(
        //put into rows
        <div>
            <div className="row-container">
                {/* create elements in rows state */}
                {rowStates.map((rowState, index) => (
                    <div key={index} className="rows">
                        {getRowComponent(index)}
                    </div>
                ))}

                <div className="result-container">
                    {results ? results.map((result, index) => (
                        <span key={index}>{result}</span>
                    ))
                    :''}
                </div>
            </div>
            <div className="button-container">
                <button style={{background:'lightyellow'}} onClick={createYellow}> + </button>
                <button style={{background:'lightyellow'}} onClick={deleteYellow}> - </button>
                <button style={{background:'lightgreen'}} onClick={getResults}> = </button>
                <button style={{background:'pink'}} onClick={clearResults}> x </button>
            </div>
        </div>
    );
}

export default RowList;
