import { wordList } from "../Data/wordList";

const ResultsProvider = ( rows ) => {
    let filteredList = [...wordList];

    for( var row of rows ) {
        filteredList = filteredList.filter((word) => {
            // check if word matches row
            for (let [i, char] of row.array.entries()) {

                char = char.toLowerCase();

                if (char === undefined || char === '' || char === ' ') continue;

                if (row.color === "green" && char !== word[i]) {
                    return false;
                }
                if (row.color === "yellow" && (!(word.includes(char)) || char === word[i])) {
                    return false;
                }
                if (row.color === "grey" && word.includes(char)) {
                    return false;
                }
            }
            return true;
        });
    }   
    return filteredList
};

export default ResultsProvider;

