import { useState, useEffect } from 'react';

function useKeyboardHandler(initialPosition, numRows, numCols, onKey) {
  const [position, setPosition] = useState(initialPosition);

  useEffect(() => {
    const handleKeyDown = (event) => {
      const { key } = event;

      if (key === 'ArrowUp' && position.row > 0) {
        setPosition({ row: position.row - 1, col: position.col });
      } else if (key === 'ArrowDown' && position.row < numRows - 1) {
        setPosition({ row: position.row + 1, col: position.col });
      } else if (key === 'ArrowLeft' && position.col > 0) {
        setPosition({ row: position.row, col: position.col - 1 });
      } else if (key === 'ArrowRight' && position.col < numCols - 1) {
        setPosition({ row: position.row, col: position.col + 1 });
      } else if (key === 'Backspace') {
        if (position.col > 0) {
          setPosition({ row: position.row, col: position.col - 1 });
          onKey(position.row, position.col - 1, '');
        }
      } else if (/^[A-Z]$/i.test(key)) {
        onKey(position.row, position.col, key);
        if (position.col < numCols - 1) {
          setPosition({ row: position.row, col: position.col + 1 });
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [position, numRows, numCols]);

  return position;
}

export default useKeyboardHandler;
