import React, { useState } from "react";
import GreenRow from '../GreenRow/GreenRow';
import YellowRow from '../YellowRow/YellowRow';
import "./RowList.css";

const RowList = () => {
    const [rowStates, setRowStates] = useState(Array(2).fill(''));

    const createYellow = () => {
        if (rowStates.length >= 5) return;
        setRowStates([...rowStates, '']);
    };

    const deleteYellow = () => {
        if (rowStates.length <= 2) return;
        setRowStates(rowStates.slice(0, -1));
    };

    const handleInputChange = (index, letterList) => {
      const newRowStates = [...rowStates];
      newRowStates[index] = letterList;
      setRowStates(newRowStates);
    };

    const getRowComponent = (index) => {
        if (index % 2 === 0) {
            return <GreenRow key={index} value={rowStates[index]} onChange={(e) => handleInputChange(index, e)} />;
        } else {
            return <YellowRow key={index} value={rowStates[index]} onChange={(e) => handleInputChange(index, e)} />;
        }
    };

    return(
        <div>
            <button onClick={createYellow}>Add Row</button>
            <button onClick={deleteYellow}>Delete Row</button>

            {/* create elements in rows state */}
            {rowStates.map((rowState, index) => (
                <div key={index}>
                    {getRowComponent(index)}
                </div>
            ))}
