// get fs module

const ResultsProvider = ( rows ) => {

    const [fileContent, setFileContent] = useState([]);

    const file = "../Data/word_list.txt";
    const reader = new FileReader();
    reader.onload = (event) => {
    const contents = event.target.result;
    const array = contents.split("\n"); // Split the contents by newline to get an array of lines
    setFileContent(array);
    };
    reader.readAsText(file);

    let wordList = fs.readFileSync("../Data/word_list.txt", "utf-8");
    console.log(wordList);

    for( let row of rows ) {
        wordList = wordList.filter((word) => {

            // check if word matches row
            for (let [i, char] of row.entries()) {
                if (row === "green" && char !== word[i]) {
                    return false;
                }
                if (row === "yellow" && (!(char in word) || char === word[i])) {
                    return false;
                }
                if (row === "grey" && char in word) {
                    return false;
                }
            }
            return true;

        });
    }   
    return wordList
};

export default ResultsProvider;

