                if (row.color === "green" && char !== word[i]) {
                    return false;
                }
                if (row.color === "yellow" && (!(char in word) || char === word[i])) {
                    return false;
                }
                if (row.color === "grey" && char in word) {
                    return false;
                }
            }
            return true;

        });
    }   
    return filteredList
};

